// src/pages/CartPage.js
import React from 'react';
import styled from 'styled-components';

// Styled components
const CartSection = styled.section`
  padding: 2rem 9%;
`;

const CartHeading = styled.h1`
  text-align: center;
  padding: 2rem 0;
  padding-bottom: 3rem;
  font-size: 3.5rem;
  color: var(--black);

  span {
    background: var(--orange);
    color: #fff;
    display: inline-block;
    padding: .5rem 3rem;
    clip-path: polygon(100% 0, 93% 50%, 100% 99%, 0% 100%, 7% 50%, 0% 0%);
  }
`;

const CartContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 2rem;
`;

const CartItem = styled.div`
  display: flex;
  gap: 2rem;
  background: #fff;
  padding: 1rem;
  box-shadow: var(--box-shadow);
  border-radius: .5rem;
`;

const CartItemDetails = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-between;
`;

const Quantity = styled.div`
  display: flex;
  gap: 1rem;
`;

const RemoveBtn = styled.button`
  margin-top: 1rem;
  padding: .5rem 1rem;
  font-size: 1.6rem;
  background: var(--orange);
  color: #fff;
  border: none;
  border-radius: .5rem;
  cursor: pointer;
`;

const CartSummary = styled.div`
  display: flex;
  justify-content: space-between;
  padding-top: 2rem;
`;

const Total = styled.p`
  font-size: 2rem;
  font-weight: bold;
`;

const CheckoutBtn = styled.a`
  font-size: 1.8rem;
  padding: .8rem 2.5rem;
  border-radius: .5rem;
  background: var(--orange);
  color: #fff;
  text-decoration: none;
  cursor: pointer;
  &:hover {
    background: #e76f00;
  }
`;

const CartPage = () => {
  const cartItems = [
    { id: 1, name: 'Fresh Apples', price: 2.99, quantity: 2, imageUrl: 'public\images\Apples.jpg' },
    { id: 2, name: 'Organic Bananas', price: 1.99, quantity: 3, imageUrl: 'public\images\Banana.jpg' },
    { id: 3, name: 'Carrots', price: 1.49, quantity: 5, imageUrl: 'public\images\Carrots.jpg' },
  ];

  const calculateTotal = () => {
    return cartItems.reduce((acc, item) => acc + item.price * item.quantity, 0).toFixed(2);
  };

  return (
    <CartSection>
      <CartHeading>Your <span>Cart</span></CartHeading>
      <CartContainer>
        {cartItems.map(item => (
          <CartItem key={item.id}>
            <img src={item.imageUrl} alt={item.name} />
            <CartItemDetails>
              <h3>{item.name}</h3>
              <p className="price">${item.price} / kg</p>
              <Quantity>
                <button>-</button>
                <span>{item.quantity}</span>
                <button>+</button>
              </Quantity>
              <RemoveBtn>Remove</RemoveBtn>
            </CartItemDetails>
          </CartItem>
        ))}
      </CartContainer>

      <CartSummary>
        <Total>Total: ${calculateTotal()}</Total>
        <CheckoutBtn href="/checkout">Proceed to Checkout</CheckoutBtn>
      </CartSummary>
    </CartSection>
  );
};

export default CartPage;
