// src/pages/ProductsPage.js
import React from 'react';
import { useCart } from '../context/CartContext';
import ProductCard from '../components/ProductCard';

const ProductsPage = () => {
  const { addToCart } = useCart();  // Use addToCart from context

  const products = [
    { id: 1, name: 'Fresh Apples', price: '$2.99 / kg', image: '/images/product-1.png' },
    { id: 2, name: 'Organic Bananas', price: '$1.99 / kg', image: '/images/product-2.png' },
    { id: 3, name: 'Carrots', price: '$1.49 / kg', image: '/images/product-3.png' },
  ];

  const handleAddToCart = (product) => {
    addToCart(product);  // Add product to cart
  };

  return (
    <div className="product-page">
      <h1>Products</h1>
      <div className="product-list">
        {products.map((product) => (
          <ProductCard
            key={product.id}
            product={product}
            onAddToCart={() => handleAddToCart(product)}  // Pass the add to cart handler
          />
        ))}
      </div>
    </div>
  );
};

export default ProductsPage;
