// src/App.js
import React from 'react';
import './index.css';

import { BrowserRouter as Router, Route, Routes, useLocation } from 'react-router-dom';
import './assets/styles.css'; // Global styles with Poppins + Theme

// Pages
import HomePage from './pages/HomePage.js';
import LoginPage from './pages/LoginPage.js';
import SignupPage from './pages/SignupPage.js';
import CartPage from './pages/CartPage.js';
import ProductsPage from './pages/ProductsPage.js';
import NotFoundPage from './pages/NotFoundPage.js';
import OrderSummaryPage from './pages/OrderSummaryPage.js';
import UserProfile from './pages/dashboard/UserProfile.js';
import OrdersPage from './pages/dashboard/OrdersPage.js';
import SettingsPage from './pages/dashboard/SettingsPage.js';
import { CartProvider } from './context/CartContext.js';

// Shared Components
import Navbar from './components/Navbar.js';
import Footer from './components/Footer.js';

// Layouts
import ProfileLayout from './pages/dashboard/ProfileLayout.js';

// Import ThemeProvider from your ThemeContext file
import { ThemeProvider } from './context/ThemeContext'; // Ensure this is correct

const Layout = ({ children }) => {
  const location = useLocation();
  const hideLayoutPaths = ['/login', '/signup'];
  const shouldHideLayout = hideLayoutPaths.includes(location.pathname);

  return (
    <>
      {!shouldHideLayout && <Navbar />}
      {children}
      {!shouldHideLayout && <Footer />}
    </>
  );
};

function App() {
  const userId = localStorage.getItem('userId');

  return (
    // Wrap your entire app with ThemeProvider to make the theme context available
    <ThemeProvider>
      <Router>
        {/* Make sure to wrap CartProvider here to provide context to the app */}
        <CartProvider>
          <Layout>
            <Routes>
              <Route path="/" element={<HomePage />} />
              <Route path="/login" element={<LoginPage />} />
              <Route path="/signup" element={<SignupPage />} />
              <Route path="/products" element={<ProductsPage />} />
              <Route path="/cart" element={<CartPage userId={userId} />} />
              <Route path="/order-summary" element={<OrderSummaryPage />} />

              {/* Dashboard layout for user profile + cart + orders + settings */}
              <Route path="/dashboard" element={<ProfileLayout />}>
                <Route path="profile" element={<UserProfile />} />
                <Route path="orders" element={<OrdersPage />} />
                <Route path="settings" element={<SettingsPage />} />
              </Route>

              {/* Catch all routes that don't match */}
              <Route path="*" element={<NotFoundPage />} />
            </Routes>
          </Layout>
        </CartProvider>
      </Router>
    </ThemeProvider>
  );
}

export default App;
