import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../pages/CartPage.css';; // Import your styles

const CartPage = () => {
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);
  const userId = localStorage.getItem('userId');
  const navigate = useNavigate();

  useEffect(() => {
    if (!userId) {
      alert("Please log in first.");
      navigate('/login');  // Redirect to login if no user is logged in
      return;
    }

    // Fetch cart data
    axios
      .get(`http://localhost:5000/api/cart/${userId}`)
      .then((response) => {
        const products = response.data.products;
        setCart(products);

        // Calculate total price
        const totalPrice = products.reduce((acc, item) => acc + (item.productId.price * item.quantity), 0);
        setTotal(totalPrice);
      })
      .catch((err) => console.error('Failed to fetch cart', err));
  }, [userId, navigate]);

  const handleCheckout = async () => {
    try {
      const orderDetails = {
        products: cart,
        total,
        userId
      };
  
      // Proceed with Razorpay
      const { data } = await axios.post('http://localhost:5000/api/payment', orderDetails);
      const options = {
        key: data.key, // Razorpay key
        amount: total * 100, // Amount in paise
        currency: 'INR',
        name: 'Grocery Store',
        description: 'Test Transaction',
        image: '/path/to/logo.png',
        order_id: data.order_id,
        handler: function (response) {
          // Handle the successful payment here
          alert("Payment Successful!");
          navigate('/order-summary', { state: { orderDetails, paymentDetails: response } }); // Pass the details to the order summary page
        },
        prefill: {
          name: "John Doe",
          email: "john@example.com",
          contact: "9876543210"
        },
        notes: {
          address: "Razorpay Corporate Office"
        },
        theme: {
          color: "#F37254"
        }
      };
      const rzp1 = new window.Razorpay(options);
      rzp1.open();
    } catch (err) {
      console.error('Checkout failed', err);
      alert('Failed to initiate checkout');
    }
  };

  return (
    <div className="cart-container">
      <h2>Your Cart</h2>
      
      {/* Display Cart Items */}
      {cart.length === 0 ? (
        <p>Your cart is empty. Start shopping now!</p>
      ) : (
        cart.map((item) => (
          <div key={item._id} className="cart-item">
            <div className="cart-item-image">
              <img src={item.productId.image} alt={item.productId.name} />
            </div>
            <div className="cart-item-details">
              <p>{item.productId.name} x {item.quantity}</p>
              <p>Price: ₹{item.productId.price * item.quantity}</p>
            </div>
          </div>
        ))
      )}

      <hr />
      <div className="total-price">
        <h3>Total: ₹{total}</h3>
        <p>Delivery Charges: ₹50</p>
        <p><strong>Grand Total: ₹{total + 50}</strong></p>
      </div>

      <div className="checkout-btn">
        <button onClick={handleCheckout} className="btn">Proceed to Checkout</button>
      </div>
    </div>
  );
};

export default CartPage;
