// src/components/ProductCard.js
import React from 'react';
import { Link } from 'react-router-dom'; // Import Link for navigation
import '../assets/styles.css';

const ProductCard = ({ image, name, price }) => {
  const addToCartHandler = () => {
    // Logic for adding the product to the cart (you can handle cart state here)
    console.log(`${name} added to cart.`);
  };

  return (
    <div className="card">
      <img src={image} alt={name} />
      <h3>{name}</h3>
      <p className="price">{price}</p>
      <button onClick={addToCartHandler} className="btn">Add to Cart</button> {/* Button for action */}
      <Link to="/cart" className="btn">Go to Cart</Link> {/* Link to Cart page */}
    </div>
  );
};

export default ProductCard;
