import React, { useEffect, useState } from 'react';
import './ProductsPage.css'; // Make sure this file exists
import axios from 'axios';

const ProductsPage = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/products') // Adjust if needed
      .then(res => setProducts(res.data))
      .catch(err => console.error(err));
  }, []);

  return (
    <div className="products-page">
      <h1 className="products-title">Fresh Fruits & Vegetables</h1>
      <div className="products-grid">
        {products.map(product => (
          <div className="product-card" key={product._id}>
            <img
              src={`/images/${product.image}`} // Make sure the image filenames match
              alt={product.name}
              className="product-image"
            />
            <h3 className="product-name">{product.name}</h3>
            <p className="product-price">â‚¹{product.price}</p>
            <button className="buy-button">Buy</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductsPage;
