import React from 'react';
import { useLocation } from 'react-router-dom';
import './OrderSummaryPage.css';


const OrderSummaryPage = () => {
  const location = useLocation();
  const { orderDetails, paymentDetails } = location.state;

  return (
    <div>
      <h2>Order Summary</h2>
      <p><strong>Order ID:</strong> {paymentDetails.razorpay_order_id}</p>
      <p><strong>Amount Paid:</strong> ₹{orderDetails.total}</p>
      <p><strong>Delivery Charge:</strong> ₹50</p>
      <p><strong>Payment Status:</strong> Successful</p>
      <h3>Total Amount: ₹{orderDetails.total + 50}</h3> {/* Add delivery charge */}
      <button className="btn">Go to Home</button>
    </div>
  );
};

export default OrderSummaryPage;
