import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../assets/styles.css';

const CartPage = () => {
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCart = async () => {
      try {
        const token = localStorage.getItem('token');
        const userId = localStorage.getItem('userId');
        if (!token || !userId) {
          navigate('/login'); // Redirect to login if not logged in
          return;
        }

        const response = await axios.get(`http://localhost:5000/api/cart/${userId}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        setCart(response.data.cart); // Assuming the response contains the cart data
        calculateTotal(response.data.cart); // Calculate total amount
      } catch (err) {
        console.error('Error fetching cart:', err);
        alert('Failed to fetch cart');
      }
    };

    fetchCart();
  }, [navigate]);

  const calculateTotal = (cart) => {
    let totalAmount = 0;
    cart.forEach((item) => {
      totalAmount += item.quantity * item.product.price; // Assuming product data is included in cart items
    });
    setTotal(totalAmount);
  };

  const handleCheckout = () => {
    // Redirect to payment page or handle checkout logic
    navigate('/checkout');
  };

  return (
    <div className="cart-container">
      <h2>Your Cart</h2>
      {cart.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <div>
          {cart.map((item) => (
            <div key={item.product._id} className="cart-item">
              <img src={item.product.image} alt={item.product.name} />
              <div>
                <h4>{item.product.name}</h4>
                <p>Price: ${item.product.price}</p>
                <p>Quantity: {item.quantity}</p>
                <p>Total: ${item.product.price * item.quantity}</p>
              </div>
            </div>
          ))}
          <h3>Total: ${total}</h3>
          <button onClick={handleCheckout} className="btn">Proceed to Checkout</button>
        </div>
      )}
    </div>
  );
};

export default CartPage;
