import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const CartPage = () => {
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);
  const userId = localStorage.getItem('userId');
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token || !userId) {
      alert('Please log in to view your cart');
      navigate('/login');
    }

    // Fetch cart data
    axios
      .get(`http://localhost:5000/api/cart/${userId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((response) => {
        setCart(response.data.products);
        const totalPrice = response.data.products.reduce(
          (acc, item) => acc + item.productId.price * item.quantity,
          0
        );
        setTotal(totalPrice);
      })
      .catch((err) => console.error('Failed to fetch cart', err));
  }, [userId, navigate]);

  const handleCheckout = async () => {
    try {
      const orderDetails = {
        products: cart,
        total,
        userId,
      };

      const { data } = await axios.post('http://localhost:5000/api/payment', orderDetails);
      const options = {
        key: data.key,
        amount: total * 100, // Amount in paise
        currency: 'INR',
        name: 'Grocery Store',
        description: 'Test Transaction',
        image: '/path/to/logo.png',
        order_id: data.order_id,
        handler: function (response) {
          alert('Payment Successful!');
          navigate('/order-summary', {
            state: { orderDetails, paymentDetails: response },
          });
        },
        prefill: {
          name: 'John Doe',
          email: 'john@example.com',
          contact: '9876543210',
        },
        notes: {
          address: 'Razorpay Corporate Office',
        },
        theme: {
          color: '#F37254',
        },
      };
      const rzp1 = new window.Razorpay(options);
      rzp1.open();
    } catch (err) {
      console.error('Checkout failed', err);
      alert('Failed to initiate checkout');
    }
  };

  return (
    <div>
      <h2>Your Cart</h2>
      {cart.map((item) => (
        <div key={item._id}>
          <p>{item.productId.name} x {item.quantity}</p>
          <p>Price: ₹{item.productId.price * item.quantity}</p>
        </div>
      ))}
      <hr />
      <h3>Total: ₹{total}</h3>
      <button onClick={handleCheckout} className="btn">Proceed to Checkout</button>
    </div>
  );
};

export default CartPage;
