// src/components/ProductCard.js
import React from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../assets/styles.css';  // Ensure correct styling is applied

const ProductCard = ({ image, name, price, productId }) => {
  const navigate = useNavigate();  // For navigation after adding product to cart
  
  // Handle adding product to cart
  const handleAddToCart = async () => {
    try {
      const token = localStorage.getItem('token');
      const userId = localStorage.getItem('userId');

      if (!token || !userId) {
        alert("Please log in first.");
        return;
      }

      // Send API request to add the product to the user's cart
      const response = await axios.post(
        'http://localhost:5000/api/cart/add',
        {
          userId,           // User ID for the authenticated user
          productId,        // Product ID being added to cart
          quantity: 1       // Default quantity is 1 (can be updated later)
        },
        {
          headers: {
            Authorization: `Bearer ${token}`  // Send token for authorization
          }
        }
      );

      if (response.data.success) {
        alert("Product added to cart successfully!");
        navigate('/cart');  // Redirect to the cart page after adding to cart
      } else {
        alert('Failed to add product to cart');
      }
    } catch (err) {
      console.error('Error adding product to cart:', err);
      alert('An error occurred while adding the product to the cart. Please try again.');
    }
  };

  return (
    <div className="card">
      <img src={image} alt={name} />
      <h3>{name}</h3>
      <p className="price">{price}</p>
      <button onClick={handleAddToCart} className="btn">Buy Now</button>
    </div>
  );
};

export default ProductCard;
