// frontend/src/pages/CartPage.js

import React, { useState, useEffect } from 'react';
import './CartPage.css'; // Import CSS for styling

const CartPage = ({ userId }) => {
  const [cart, setCart] = useState([]);
  const [totalAmount, setTotalAmount] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  console.log("User ID passed to CartPage:", userId);

  useEffect(() => {
    const fetchCart = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/cart/${userId}`);
        if (!response.ok) throw new Error('Failed to fetch cart');
        const data = await response.json();

        if (data && data.products) {
          setCart(data.products);
          const total = data.products.reduce((acc, item) => {
            return acc + (item.productId.price * item.quantity);
          }, 0);
          setTotalAmount(total);
        } else {
          setCart([]);
          setTotalAmount(0);
        }
      } catch (error) {
        console.error('Error fetching cart:', error);
        setError('Could not fetch the cart. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchCart();
  }, [userId]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="cart-container">
      <h2>Your Cart</h2>
      <div className="cart-grid">
        {cart.length > 0 ? (
          cart.map((item, index) => (
            <div className="cart-item" key={index}>
              <img src={item.productId.image} alt={item.productId.name} />
              <div className="info">
                <h4>{item.productId.name}</h4>
                <p>Quantity: {item.quantity}</p>
                <p>Price: ${item.productId.price}</p>
                <p>Subtotal: ${(item.quantity * item.productId.price).toFixed(2)}</p>
              </div>
            </div>
          ))
        ) : (
          <p>Your cart is empty.</p>
        )}
      </div>
      <h3>Total: ${totalAmount.toFixed(2)}</h3>
    </div>
  );
};

export default CartPage;
