// frontend/src/pages/CartPage.js

import React, { useState, useEffect } from 'react';
import './CartPage.css'; // Link to styles

const CartPage = ({ userId }) => {
  const [cart, setCart] = useState([]);
  const [totalAmount, setTotalAmount] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!userId) {
      setError("No user ID provided. Please log in.");
      setLoading(false);
      return;
    }

    const fetchCart = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/cart/${userId}`);
        if (!response.ok) throw new Error('Failed to fetch cart');

        const data = await response.json();

        if (data && data.products) {
          setCart(data.products);

          const total = data.products.reduce((acc, item) => {
            const price = item.productId?.price || 0;
            return acc + price * item.quantity;
          }, 0);

          setTotalAmount(total);
        } else {
          setCart([]);
          setTotalAmount(0);
        }

      } catch (error) {
        console.error('Error fetching cart:', error);
        setError('Could not fetch the cart. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchCart();
  }, [userId]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="cart-page">
      <h2>Your Cart</h2>
      <div className="cart-items">
        {cart.length > 0 ? (
          cart.map((item, index) => (
            <div key={index} className="cart-card">
              <img src={item.productId?.image} alt={item.productId?.name || "Product"} />
              <div className="cart-details">
                <h3>{item.productId?.name || 'Unknown Product'}</h3>
                <p>Price: ${item.productId?.price?.toFixed(2) || '0.00'}</p>
                <p>Quantity: {item.quantity}</p>
                <p>Subtotal: ${(item.productId?.price * item.quantity).toFixed(2)}</p>
              </div>
            </div>
          ))
        ) : (
          <p>Your cart is empty.</p>
        )}
      </div>

      <div className="cart-summary">
        <h3>Order Summary</h3>
        <p>Total Items: {cart.reduce((acc, item) => acc + item.quantity, 0)}</p>
        <p>Total Amount: <strong>${totalAmount.toFixed(2)}</strong></p>
      </div>
    </div>
  );
};

export default CartPage;
