// frontend/src/pages/CartPage.js

import React, { useState, useEffect } from 'react';

const CartPage = ({ userId }) => {
  const [cart, setCart] = useState([]);
  const [totalAmount, setTotalAmount] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!userId) {
      setError("No user ID provided. Please log in.");
      setLoading(false);
      return;
    }

    const fetchCart = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/cart/${userId}`);

        if (!response.ok) {
          throw new Error('Failed to fetch cart');
        }

        const data = await response.json();

        if (data && data.products) {
          setCart(data.products);

          // Calculate total amount
          const total = data.products.reduce((acc, item) => {
            const price = item.productId?.price || 0;
            return acc + price * item.quantity;
          }, 0);

          setTotalAmount(total);
        } else {
          setCart([]);
          setTotalAmount(0);
        }

      } catch (error) {
        console.error('Error fetching cart:', error);
        setError('Could not fetch the cart. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchCart();
  }, [userId]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div>
      <h2>Your Cart</h2>
      {cart.length > 0 ? (
        cart.map((item, index) => (
          <div key={index}>
            <p>{item.productId?.name || 'Unknown Product'} - {item.quantity} x ${item.productId?.price?.toFixed(2) || '0.00'}</p>
          </div>
        ))
      ) : (
        <p>Your cart is empty.</p>
      )}
      <h3>Total: ${totalAmount.toFixed(2)}</h3>
    </div>
  );
};

export default CartPage;
