// frontend/src/pages/ProductsPage.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ProductsPage.css'; // We'll create this next
import { useNavigate } from 'react-router-dom';

const ProductsPage = () => {
  const [products, setProducts] = useState([]);
  const navigate = useNavigate();

  // Dummy product list (15 items) — this can be replaced later by fetching from /api/products
  useEffect(() => {
    const productList = [
      { _id: '1', name: 'Apple', price: 2, image: '/images/Apple.jpg' },
      { _id: '2', name: 'Banana', price: 1, image: '/images/Banana.jpg' },
      { _id: '3', name: 'Carrot', price: 1.5, image: '/images/Carrot.jpg' },
      { _id: '4', name: 'Tomato', price: 2, image: '/images/tomato.jpg' },
      { _id: '5', name: 'Potato', price: 1, image: '/images/potato.jpg' },
      { _id: '6', name: 'Onion', price: 1.2, image: '/images/product-2.png' },
      { _id: '7', name: 'Mango', price: 3, image: '/images/mango.jpg' },
      { _id: '8', name: 'Spinach', price: 1.8, image: '/images/spinach.jpg' },
      { _id: '9', name: 'Cucumber', price: 1.6, image: '/images/cucumber.jpg' },
      { _id: '10', name: 'Broccoli', price: 2.5, image: '/images/broccoli.jpg' },
      { _id: '11', name: 'Watermelon', price: 2.2, image: '/images/cart-img-1.png' },
      { _id: '12', name: 'Orange', price: 2.1, image: 'images/Orange.jpg' },
      { _id: '13', name: 'Lettuce', price: 1.9, image: '/images/lettuce.jpg' },
      { _id: '14', name: 'Papaya', price: 2.7, image: '/images/papaya.jpg' },
      { _id: '15', name: 'Pineapple', price: 3.5, image: '/images/Pineapple.jpg' }
    ];
    setProducts(productList);
  }, []);

  const handleBuy = async (product) => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please log in to add items to cart.');
      navigate('/login');
      return;
    }

    try {
      await axios.post(
        'http://localhost:5000/api/cart',
        {
          productId: product._id,
          name: product.name,
          price: product.price,
          quantity: 1,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      alert(`${product.name} added to cart!`);
    } catch (error) {
      console.error(error);
      alert('Failed to add to cart.');
    }
  };

  return (
    <div className="products-page">
      <h2 className="products-title">Fresh Fruits & Vegetables</h2>
      <div className="products-grid">
        {products.map((product) => (
          <div className="product-card" key={product._id}>
            <img src={product.image} alt={product.name} />
            <h3>{product.name}</h3>
            <p>₹{product.price.toFixed(2)}</p>
            <button onClick={() => handleBuy(product)}>Buy</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductsPage;
